import random

class Drone:
    def __init__(self, x, y):
        self.x = x
        self.y = y

    def move_up(self):
        self.y += 1

    def move_down(self):
        self.y -= 1

    def move_left(self):
        self.x -= 1

    def move_right(self):
        self.x += 1

    def scan(self):
        target_x = random.randint(0, 10)
        target_y = random.randint(0, 10)
        return (self.x == target_x) and (self.y == target_y)

