import random

class Drone:
    def __init__(self, x, y):
        self.x = x
        self.y = y
        self.battlefield = None

    def move(self):
        direction = random.choice(['N', 'E', 'S', 'W'])
        if direction == 'N':
            self.y += 1
        elif direction == 'E':
            self.x += 1
        elif direction == 'S':
            self.y -= 1
        elif direction == 'W':
            self.x -= 1

    def drop_bomb(self):
        if self.battlefield:
            self.battlefield[self.x][self.y] = 'X'

    def run_simulation(self, duration):
        for _ in range(duration):
            self.move()
            self.drop_bomb()

def create_battlefield(width, height):
    return [[' ' for _ in range(height)] for _ in range(width)]

def print_battlefield(battlefield):
    for row in battlefield:
        for cell in row:
            print(cell, end='')
        print()

def main():
    width = 10
    height = 10
    battlefield = create_battlefield(width, height)
    drone = Drone(0, 0)
    drone.battlefield = battlefield
    drone.run_simulation(10)
    print_battlefield(battlefield)

if __name__ == '__main__':
