def count_tokens():
    filename = input("Enter the name of the file: ")
    token_count = {}

    try:
        with open(filename, 'r') as file:
            for line in file:
                tokens = line.split(" ")
                for token in tokens:
                    token = token.strip()
                    if token:
                        if token in token_count:
                            token_count[token] += 1
                        else:
                            token_count[token] = 1

        search_token = input("Enter a token: ")
        if search_token in token_count:
            print(f"The token '{search_token}' appears {token_count[search_token]} times.")
        else:
            print("0")

    except FileNotFoundError:
        print("File not found.")
    except:
        print("Error reading file.")

