def print_numbers(first_int, second_int):
    if second_int < first_int:
        return "Second integer can't be less than the first."

    output = str(first_int)  # Start with the first integer

    while first_int <= second_int:
        first_int += 10
        output += " " + str(first_int)  # Add subsequent increments of 10

    return output

